from selenium import webdriver
from selenium.webdriver.common.keys import Keys
from selenium.webdriver.common.by import By
from selenium.webdriver.support.ui import WebDriverWait
from selenium.webdriver.support import expected_conditions as EC
import time
import urllib.request
import os
import ssl

# Desactivar la verificación del certificado SSL
ssl._create_default_https_context = ssl._create_unverified_context

def buscar_imagenes_y_guardar(query, nombre, indice_imagen=2, directorio_destino="imagenes"):
    # Configuración del navegador
    driver = webdriver.Chrome()  # Asegúrate de tener el controlador de Chrome instalado y actualizado

    # Navegar a la página de imágenes de Google
    driver.get("https://www.google.com/imghp")

    # Encontrar el cuadro de búsqueda de imágenes
    search_box = driver.find_element("name", "q")

    # Ingresar la consulta y presionar Enter
    search_box.send_keys(query)
    search_box.send_keys(Keys.RETURN)

    # Esperar un momento para que los resultados se carguen
    time.sleep(2)

    for intento in range(1, 3):  # Intentar primero con la primera imagen y luego con la segunda
        try:
            # Encontrar el elemento de imagen correspondiente al índice especificado
            image_elements = WebDriverWait(driver, 10).until(
                EC.presence_of_all_elements_located((By.CSS_SELECTOR, ".rg_i"))
            )

            if len(image_elements) >= indice_imagen:
                # Hacer clic en el elemento de imagen correspondiente al índice
                image_elements[indice_imagen - 1].click()
            else:
                print(f"No hay suficientes imágenes para el índice {indice_imagen}")
                driver.quit()
                return

            # Esperar a que aparezca el elemento .sFlh5c
            sFlh5c_element = WebDriverWait(driver, 10).until(
                EC.presence_of_element_located((By.CLASS_NAME, "sFlh5c"))
            )

            # Obtener la URL de la imagen desde el elemento .sFlh5c
            image_url = sFlh5c_element.get_attribute("src")

            # Crear el directorio de destino si no existe
            if not os.path.exists(directorio_destino):
                os.makedirs(directorio_destino)

            # Configurar un User-Agent en la solicitud
            headers = {'User-Agent': 'Mozilla/5.0 (Windows NT 10.0; Win64; x64) AppleWebKit/537.36 (KHTML, like Gecko) Chrome/58.0.3029.110 Safari/537.3'}
            req = urllib.request.Request(image_url, headers=headers)

            # Agregar un retraso antes de la descarga
            time.sleep(5)  # Puedes ajustar este valor según sea necesario

            # Descargar y guardar la imagen con el nombre proporcionado
            image_path = os.path.join(directorio_destino, f"{nombre}.jpg")
            with urllib.request.urlopen(req) as response, open(image_path, 'wb') as out_file:
                data = response.read()
                out_file.write(data)

            print(f"Imagen '{nombre}' (índice {indice_imagen}) descargada y guardada en {image_path}")

        except Exception as e:
            print(f"Error en el intento {intento} al descargar la imagen para '{nombre}': {e}")
        else:
            # Si la descarga fue exitosa, salir del bucle
            break

    # Cerrar el navegador
    driver.quit()

def buscar_imagenes_para_lista(consultas, nombres, cantidad=1, indice_imagen=2):
    if len(consultas) != len(nombres):
        print("Error: La lista de consultas y la lista de nombres deben tener la misma longitud.")
        return

    for consulta, nombre in zip(consultas, nombres):
        buscar_imagenes_y_guardar(consulta, nombre, indice_imagen)

